import frappe
from frappe.utils import flt, getdate

data = frappe.db.get_all(
    'Income Entry',
    fields=['date', 'amount']
)

# Aggregate by month
monthly_revenue = {}
for d in data:
    month = getdate(d['date']).strftime("%Y-%m")  # Python way
    monthly_revenue[month] = monthly_revenue.get(month, 0) + flt(d['amount'])

# Convert to list for report
report_data = []
for month, total in sorted(monthly_revenue.items()):
    report_data.append({
        'Month': month,
        'Total Revenue': total
    })

report_data
